import React, { useState, useEffect } from "react";
import Accordion from "@mui/material/Accordion";
import AccordionSummary from "@mui/material/AccordionSummary";
import AccordionDetails from "@mui/material/AccordionDetails";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import TextField from "@mui/material/TextField";
import axios from "axios";
import Button from "@mui/material/Button";

export const UserPosts = () => {
  const [posts, setPosts] = useState([]);
  const [comment, setComment] = useState("");
  const [search, setSearch] = useState("");
  const [submitForm, setSubmitForm] = useState(false);

//   const handleSearch = () => {
//     setSearch(e.target.value);
//   };

  const handleSearch =(e)=> {
    const searchString = e.target.value
    if (searchString.length > 0){
      const searchData = userPosts.filter((eachTitle)=>eachTitle.title.toLowerCase().includes(searchString));
      setUserPosts(searchData);
    }
    else{
      setUserPosts(filteredData)
    }
    setQuery(searchString);
  }

  const handleComment = (e) => {
    setComment(e.target.value);
  };
  const handleSubmit = () => {
    setSubmitForm(true);
  };

  const postsList = () => {
    axios.get("https://jsonplaceholder.typicode.com/posts").then((res) => {
      console.log("res", res.data);
      setPosts(res.data);
    });
  };

  useEffect(() => {
    postsList();
  }, []);

  return (
    <div>
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1-content"
          id="panel1-header"
        >
          User Posts
        </AccordionSummary>
        <AccordionDetails>
          <TextField onChange={handleSearch}
          placeholder="Search Posts" label="search" id="fullWidth" />
          {posts.map((post) => {
            return (
              <>
                <li>userId : {post.userId}</li>
                <li>id: {post.id}</li>
                <li>title: {post.title}</li>
                <li>body: {post.body}</li>
                <h3>{comment}</h3>
                <TextField
                  onChange={(e) => handleComment(e)}
                  placeholder="Enter Comments"
                  label="Comments"
                  id="fullWidth"
                />
              </>
            );
          })}
          {/* <Button onClick={handleSubmit()} variant="outlined">
            POST COMMENTS
          </Button>
          {submitForm ? <h1>{comment}</h1> : ""} */}
        </AccordionDetails>
      </Accordion>
    </div>
  );
};

export default UserPosts;
