import React, { useState, useEffect } from "react";
import Accordion from "@mui/material/Accordion";
import AccordionSummary from "@mui/material/AccordionSummary";
import AccordionDetails from "@mui/material/AccordionDetails";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import TextField from "@mui/material/TextField";
import axios from "axios";
import Button from "@mui/material/Button";
import SearchIcon from "@mui/icons-material/Search";
import InputAdornment from "@mui/material/InputAdornment";

export const UserPosts = () => {
  const [posts, setPosts] = useState([]);
  const [album, setAlbums] = useState([]);
  const [comment, setComment] = useState("");
  const [submitForm, setSubmitForm] = useState(false);
  const [commentId, setCommentId] = useState(false);
  const [filteredData, setFilteredData] = useState([]);
  const [query, setQuery] = useState("");

  //   const handleSearch = () => {
  //     setSearch(e.target.value);
  //   };

  const handleSearch = (e) => {
    const searchString = e.target.value;
    if (searchString.length > 0) {
      const searchData = posts.filter((eachTitle) =>
        eachTitle.title.toLowerCase().includes(searchString)
      );
      setPosts(searchData);
    } else {
      setPosts(filteredData);
    }
    setQuery(searchString);
  };

  const handleComment = (e, id) => {
    posts.map((post) => {
      if (post.id === id) {
        setComment(e.target.value);
        setCommentId(true);
      }
    });
  };
  const handleSubmit = () => {
    setSubmitForm(true);
  };

  const postsList = () => {
    axios
      .get("https://jsonplaceholder.typicode.com/posts?_limit=5")
      .then((res) => {
        console.log("res", res.data);
        setPosts(res.data);
        setFilteredData(res.data);
      });
  };

  const albums = () => {
    axios
      .get("https://jsonplaceholder.typicode.com/albums?_limit=5")
      .then((res) => {
        console.log("res", res.data);
        setAlbums(res.data);
      });
  };

  useEffect(() => {
    postsList();
    albums();
  }, []);

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
      }}
    >
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1-content"
          id="panel1-header"
        >
          User Posts
        </AccordionSummary>
        <AccordionDetails>
          <TextField
            value={query}
            onChange={handleSearch}
            placeholder="Search Posts"
            label="search"
            id="fullWidth"
            InputProps={{
              startAdornment: (
                <InputAdornment position="end">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
          />

          {posts.map((post) => {
            return (
              <>
                {/* <li>userId : {post.userId}</li>
                <li>id: {post.id}</li> */}
                <li>title: {post.title}</li>
                <li>body: {post.body}</li>
                {commentId ? <h3>{comment}</h3> : ""}
                <TextField
                  onChange={(e) => handleComment(e, post.id)}
                  placeholder="Enter Comments"
                  label="Comments"
                  id="fullWidth"
                />
              </>
            );
          })}
          <h3>{comment}</h3>
          {/* <Button onClick={handleSubmit()} variant="outlined">
            POST COMMENTS
          </Button>
          {submitForm ? <h1>{comment}</h1> : ""} */}
        </AccordionDetails>
      </Accordion>
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1-content"
          id="panel1-header"
        >
          User Album
        </AccordionSummary>
        <AccordionDetails>
          {album.map((item) => {
            return (
              <div>
                <li>{item.title}</li>
              </div>
            );
          })}
        </AccordionDetails>
      </Accordion>
    </div>
  );
};

export default UserPosts;
