import React, { useState } from 'react'
import Comments from './Comments'

export default function Post(props) {
  const [comments,setComments] = useState([])

  const addPost = (comment) => {
    setComments([...comments,comment])
  }

  const handlDelete = (comment) => {
  let cloned = [...comments] 
  let index = cloned.findIndex((c) => c === comment)
  if(index != -1){
    cloned.splice()
  }
  }

  return (
    <div>
            <>
              <li>title: {props.post.title}</li>
              <li>body: {props.post.body}</li>
              {comments.length>0 ? comments.map((com) => <div onClick={()=>{handlDelete(com)}}>{com}</div>) :<></>}
              <Comments addPost={addPost} />       
            </>    
      </div>
  )
}
