import React, { useState, useEffect } from "react";
import Accordion from "@mui/material/Accordion";
import AccordionSummary from "@mui/material/AccordionSummary";
import AccordionDetails from "@mui/material/AccordionDetails";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import TextField from "@mui/material/TextField";
import axios from "axios";
import Button from "@mui/material/Button";
import SearchIcon from "@mui/icons-material/Search";
import InputAdornment from "@mui/material/InputAdornment";

export const UserPosts = () => {
  const [posts, setPosts] = useState([]);
  const [album, setAlbums] = useState([]);
  const [imageUrl, setImageUrl] = useState("");
  const [comment, setComment] = useState("");
  const [submitForm, setSubmitForm] = useState(false);
  const [photo, setPhotos] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [query, setQuery] = useState("");

  //   const handleSearch = () => {
  //     setSearch(e.target.value);
  //   };

  const handleSearch = (e) => {
    const searchString = e.target.value;
    if (searchString.length > 0) {
      const searchData = posts.filter((eachTitle) =>
        eachTitle.title.toLowerCase().includes(searchString)
      );
      setPosts(searchData);
    } else {
      setPosts(filteredData);
    }
    setQuery(searchString);
  };

  const handleComment = (e) => {
    setComment(e.target.value);
  };

  const handleSubmit = () => {
    setSubmitForm(true);
  };

  const handleImages = (item) => {
    photo.map((item) => {
      if (item.albumId === item.userId) {
        setImageUrl(item.url);
      }
    });
  };

  const handleCommentChange = (e) => {
    setComment(e.target.value);
  };

  const handleCommentSubmit = async () => {
    if (comment.trim() !== '') {
      try {
        const response = await fetch(`https://your-api-url/posts/${post.id}/comments`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ text: newComment, author: 'New User' }),
        });
        if (!response.ok) {
          throw new Error('Failed to add comment');
        }
        setNewComment('');
        fetchComments(); // Refresh comments after adding new comment
      } catch (error) {
        console.error(error);
      }
    }
  };

  const postsList = () => {
    axios
      .get("https://jsonplaceholder.typicode.com/posts?_limit=5")
      .then((res) => {
        console.log("res", res.data);
        setPosts(res.data);
        setFilteredData(res.data);
      });
  };

  const albums = () => {
    axios
      .get("https://jsonplaceholder.typicode.com/albums?_limit=20")
      .then((res) => {
        console.log("res", res.data);
        setAlbums(res.data);
      });
  };

  const photos = () => {
    axios
      .get("https://jsonplaceholder.typicode.com/photos?_limit=20")
      .then((res) => {
        console.log("res", res.data);
        setPhotos(res.data);
      });
  };

  useEffect(() => {
    postsList();
    albums();
    photos();
  }, []);

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
      }}
    >
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1-content"
          id="panel1-header"
        >
          User Posts
        </AccordionSummary>
        <AccordionDetails>
          <TextField
            value={query}
            onChange={handleSearch}
            placeholder="Search Posts"
            label="search"
            id="fullWidth"
            InputProps={{
              startAdornment: (
                <InputAdornment position="end">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
          />

          {posts.map((post, number) => {
            return (
              <>
                {/* <li>userId : {post.userId}</li>
                <li>id: {post.id}</li> */}
                <li>title: {post.title}</li>
                <li>body: {post.body}</li>
                <h3>{comment}</h3>
                <TextField
                  onChange={(e) => handleComment(e)}
                  placeholder="Enter Comments"
                  value={comment}
                  label="Comments"
                  id="fullWidth"
                />
              </>
            );
          })}
           <div>
        <input type="text" value={comment} onChange={handleCommentChange} />
        <button onClick={handleCommentSubmit}>Add Comment</button>
      </div>
          {/* <h3>{comment}</h3> */}
          {/* <Button onClick={handleSubmit()} variant="outlined">
            POST COMMENTS
          </Button>
          {submitForm ? <h1>{comment}</h1> : ""} */}
        </AccordionDetails>
      </Accordion>
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1-content"
          id="panel1-header"
        >
          User Album
        </AccordionSummary>
        <AccordionDetails>
          {photo.map((item) => {
            return (
              <div>
                <li>{item.title}</li>
                <img alt="" src={item.url} />
              </div>
            );
          })}
        </AccordionDetails>
      </Accordion>
    </div>
  );
};

export default UserPosts;
