import React, { useState } from "react";
// import Comments from "./Comments";
import Paper from "@mui/material/Paper";
import DeleteIcon from "@mui/icons-material/Delete";
import Comments from "./CommentBox";
import DeleteForeverRoundedIcon from '@mui/icons-material/DeleteForeverRounded';
import InputAdornment from "@mui/material/InputAdornment";

export default function Post(props) {
  const [comments, setComments] = useState([]);

  const addPost = (comment) => {
    setComments([...comments, comment]);
  };

  const handlDelete = (comment) => {
    let cloned = [...comments];
    let index = cloned.findIndex((c) => c === comment);
    if (index !== -1) {
      cloned.splice(index, 1);
      setComments(cloned);
    }
  };

  return (
    <div>
      <>
        <Paper elevation={3} padding={"5px"}>
          <h3> {props.post.title}</h3>
          <p> {props.post.body}</p>
          {comments.length > 0 ? (
            comments.map((com) => (
              <div>
                {com}{" "}
                <button

                >


                <InputAdornment position="start">
                < DeleteForeverRoundedIcon  onClick={() => {
                    handlDelete(com);
                  }}/>
            </InputAdornment>
                </button>
              </div>
            ))
          ) : (
            <></>
          )}
          <Comments addPost={addPost} />
        </Paper>
      </>
    </div>
  );
}
